import React from 'react';
import Chat from './Chat'
import Participant from './Participant';
import Stage from './Stage'

function App(props) {

  const onStage = props.list.filter(participant => participant.onStage);
  console.log(onStage);

  props.chatEvents.forEach(chat => {
    props.list.forEach((participant => {
      if (chat.participantId === participant.id) {
        chat.name = participant.name;
        chat.avatar = participant.avatar;
      }
    }))
  })

  console.log(props.chatEvents);

  return (
    <main className='App'>
      <div>
        {props.chatEvents.map((chat, i) => {
          return <Chat key={i} name={chat.name} avatar={chat.avatar} />
        })}
      </div>
      <div>
        {props.list.map(participant => {
          return <Participant key={participant.id} name={participant.name} avatar={participant.avatar} inSession={participant.inSession} onStage={participant.onStage} type={participant.type} message={participant.message} time={participant.time} timestamp={participant.timestamp} />
        })}
      </div>
      <div>
        {onStage.map((participant, i) => {
          return <Stage key={i} avatar={participant.avatar} name={participant.name} />
        })}
      </div>
    </main>
  );
}

export default App;
